INSERT INTO cms_pages VALUES(2,1,'About Us','about',replace('<div class="about-page-wrapper">\n\n    <!-- Mission Statement -->\n    <section style="margin-bottom: 60px; text-align: center; max-width: 800px; margin-left: auto; margin-right: auto;">\n        <h2 style="font-size: 2.5rem; margin-bottom: 20px; font-weight: 700;">We''re building the operating system for the future.</h2>\n        <p style="font-size: 1.25rem; color: #a1a1aa; line-height: 1.6;">Gaia Alpha was born from a simple idea: Enterprise software doesn''t have to be complicated. We believe in the power of simplicity, the speed of vanilla code, and the freedom of open source.</p>\n    </section>\n\n    <hr style="border: 0; border-top: 1px solid rgba(255,255,255,0.1); margin: 60px 0;" />\n\n    <!-- Our Story & Vision Grid -->\n    <section style="display: grid; grid-template-columns: repeat(auto-fit, minmax(300px, 1fr)); gap: 40px; margin-bottom: 80px;">\n        <div>\n            <h3 style="font-size: 1.8rem; margin-bottom: 20px; color: #fff;">Our Story</h3>\n            <p style="color: #a1a1aa; line-height: 1.7; margin-bottom: 20px;">Founded in 2024, Gaia Alpha started as an experiment to see if we could build a full-stack framework without the bloat of modern toolchains. What began as a lightweight prototype quickly evolved into a robust ecosystem used by forward-thinking teams to deploy applications in record time.</p>\n            <p style="color: #a1a1aa; line-height: 1.7;">Today, we are a global community of developers, designers, and dreamers working together to redefine what''s possible on the web.</p>\n        </div>\n        <div>\n            <img src="https://images.unsplash.com/photo-1522071820081-009f0129c71c?auto=format&fit=crop&w=800&q=80" alt="Team working together" style="width: 100%; border-radius: 12px; border: 1px solid rgba(255,255,255,0.1);">\n        </div>\n    </section>\n\n    <!-- Values -->\n    <section style="margin-bottom: 80px;">\n        <h3 style="text-align: center; font-size: 2rem; margin-bottom: 50px; color: #fff;">Our Core Values</h3>\n        <div style="display: grid; grid-template-columns: repeat(auto-fit, minmax(250px, 1fr)); gap: 30px;">\n            <div style="background: rgba(255,255,255,0.03); padding: 30px; border-radius: 12px; border: 1px solid rgba(255,255,255,0.05);">\n                <div style="font-size: 2rem; margin-bottom: 15px;">‚ö°</div>\n                <h4 style="font-size: 1.25rem; margin-bottom: 10px; color: #fff;">Radical Simplicity</h4>\n                <p style="color: #a1a1aa;">We ruthlessly eliminate complexity. If it doesn''t add value, it doesn''t belong in the codebase.</p>\n            </div>\n            <div style="background: rgba(255,255,255,0.03); padding: 30px; border-radius: 12px; border: 1px solid rgba(255,255,255,0.05);">\n                <div style="font-size: 2rem; margin-bottom: 15px;">üîì</div>\n                <h4 style="font-size: 1.25rem; margin-bottom: 10px; color: #fff;">Open by Default</h4>\n                <p style="color: #a1a1aa;">Transparancy builds trust. We build in public and empower our community to contribute.</p>\n            </div>\n            <div style="background: rgba(255,255,255,0.03); padding: 30px; border-radius: 12px; border: 1px solid rgba(255,255,255,0.05);">\n                <div style="font-size: 2rem; margin-bottom: 15px;">ü§ù</div>\n                <h4 style="font-size: 1.25rem; margin-bottom: 10px; color: #fff;">Users First</h4>\n                <p style="color: #a1a1aa;">Technology serves people, not the other way around. Every decision starts with the user experience.</p>\n            </div>\n        </div>\n    </section>\n\n    <!-- Team -->\n    <section>\n        <h3 style="text-align: center; font-size: 2rem; margin-bottom: 50px; color: #fff;">Meet the Team</h3>\n        <div style="display: grid; grid-template-columns: repeat(auto-fill, minmax(200px, 1fr)); gap: 30px; text-align: center;">\n            <div>\n                <img src="https://i.pravatar.cc/300?u=a042581f4e29026024d" alt="CEO" style="width: 100px; height: 100px; border-radius: 50%; object-fit: cover; margin-bottom: 15px; border: 2px solid #6366f1;">\n                <h4 style="color: #fff; margin-bottom: 5px;">Alex Rivera</h4>\n                <p style="color: #a1a1aa; font-size: 0.9rem;">Founder & CEO</p>\n            </div>\n            <div>\n                <img src="https://i.pravatar.cc/300?u=a042581f4e29026704d" alt="CTO" style="width: 100px; height: 100px; border-radius: 50%; object-fit: cover; margin-bottom: 15px; border: 2px solid #10b981;">\n                <h4 style="color: #fff; margin-bottom: 5px;">Sarah Chen</h4>\n                <p style="color: #a1a1aa; font-size: 0.9rem;">CTO</p>\n            </div>\n            <div>\n                <img src="https://i.pravatar.cc/300?u=a04258114e29026302d" alt="Designer" style="width: 100px; height: 100px; border-radius: 50%; object-fit: cover; margin-bottom: 15px; border: 2px solid #f59e0b;">\n                <h4 style="color: #fff; margin-bottom: 5px;">Marcus Johnson</h4>\n                <p style="color: #a1a1aa; font-size: 0.9rem;">Lead Designer</p>\n            </div>\n             <div>\n                <img src="https://i.pravatar.cc/300?u=a042581f4e29026024a" alt="Developer" style="width: 100px; height: 100px; border-radius: 50%; object-fit: cover; margin-bottom: 15px; border: 2px solid #ec4899;">\n                <h4 style="color: #fff; margin-bottom: 5px;">Elena Rodriguez</h4>\n                <p style="color: #a1a1aa; font-size: 0.9rem;">Senior Eng.</p>\n            </div>\n        </div>\n    </section>\n\n</div>','\n',char(10)),'2025-12-14 00:48:39','2025-12-14 00:48:39',NULL,'page',NULL,'default_site','Learn about Gaia Alpha''s mission to revolutionize enterprise software with an open, scalable, and secure operating system.','about gaia alpha, enterprise software, open source mission, company values');
INSERT INTO cms_pages VALUES(3,1,'Careers','careers',replace('<div class="careers-wrapper">\n    <div style="text-align: center; max-width: 800px; margin: 0 auto 60px;">\n        <h2 style="font-size: 2.5rem; margin-bottom: 20px; font-weight: 700; color: #fff;">Build the future with us.</h2>\n        <p style="font-size: 1.25rem; color: #a1a1aa; line-height: 1.6;">We''re on a mission to simplify enterprise software. We look for people who are curious, collaborative, and driven to solve hard problems.</p>\n    </div>\n\n    <!-- Benefits -->\n    <div style="display: grid; grid-template-columns: repeat(auto-fit, minmax(200px, 1fr)); gap: 30px; margin-bottom: 80px;">\n        <div style="text-align: center;">\n            <div style="font-size: 2rem; margin-bottom: 10px;">üåç</div>\n            <h4 style="color: #fff; margin-bottom: 5px;">Remote First</h4>\n            <p style="color: #a1a1aa; font-size: 0.9rem;">Work from anywhere in the world.</p>\n        </div>\n        <div style="text-align: center;">\n            <div style="font-size: 2rem; margin-bottom: 10px;">üè•</div>\n            <h4 style="color: #fff; margin-bottom: 5px;">Full Health</h4>\n            <p style="color: #a1a1aa; font-size: 0.9rem;">100% coverage for you and family.</p>\n        </div>\n        <div style="text-align: center;">\n            <div style="font-size: 2rem; margin-bottom: 10px;">‚úàÔ∏è</div>\n            <h4 style="color: #fff; margin-bottom: 5px;">Unlimited PTO</h4>\n            <p style="color: #a1a1aa; font-size: 0.9rem;">Take the time you need to recharge.</p>\n        </div>\n        <div style="text-align: center;">\n            <div style="font-size: 2rem; margin-bottom: 10px;">üìà</div>\n            <h4 style="color: #fff; margin-bottom: 5px;">Equity</h4>\n            <p style="color: #a1a1aa; font-size: 0.9rem;">Everyone is an owner at Gaia Alpha.</p>\n        </div>\n    </div>\n\n    <!-- Open Positions -->\n    <h3 style="font-size: 1.8rem; margin-bottom: 30px; color: #fff;">Open Positions</h3>\n    <div style="display: flex; flex-direction: column; gap: 20px;">\n        \n        <div style="display: flex; justify-content: space-between; align-items: center; padding: 25px; background: rgba(255,255,255,0.03); border: 1px solid rgba(255,255,255,0.05); border-radius: 12px; transition: background 0.2s;">\n            <div>\n                <h4 style="color: #fff; font-size: 1.2rem; margin-bottom: 5px;">Senior Frontend Developer</h4>\n                <p style="color: #a1a1aa; margin: 0;">Engineering ‚Ä¢ Remote (Global)</p>\n            </div>\n            <a href="#" style="color: #818cf8; text-decoration: none; font-weight: 600;">Apply &rarr;</a>\n        </div>\n\n        <div style="display: flex; justify-content: space-between; align-items: center; padding: 25px; background: rgba(255,255,255,0.03); border: 1px solid rgba(255,255,255,0.05); border-radius: 12px; transition: background 0.2s;">\n            <div>\n                <h4 style="color: #fff; font-size: 1.2rem; margin-bottom: 5px;">Backend Engineer (PHP)</h4>\n                <p style="color: #a1a1aa; margin: 0;">Engineering ‚Ä¢ Remote (US/EU)</p>\n            </div>\n            <a href="#" style="color: #818cf8; text-decoration: none; font-weight: 600;">Apply &rarr;</a>\n        </div>\n\n        <div style="display: flex; justify-content: space-between; align-items: center; padding: 25px; background: rgba(255,255,255,0.03); border: 1px solid rgba(255,255,255,0.05); border-radius: 12px; transition: background 0.2s;">\n            <div>\n                <h4 style="color: #fff; font-size: 1.2rem; margin-bottom: 5px;">Product Designer</h4>\n                <p style="color: #a1a1aa; margin: 0;">Design ‚Ä¢ Remote (Global)</p>\n            </div>\n            <a href="#" style="color: #818cf8; text-decoration: none; font-weight: 600;">Apply &rarr;</a>\n        </div>\n\n         <div style="display: flex; justify-content: space-between; align-items: center; padding: 25px; background: rgba(255,255,255,0.03); border: 1px solid rgba(255,255,255,0.05); border-radius: 12px; transition: background 0.2s;">\n            <div>\n                <h4 style="color: #fff; font-size: 1.2rem; margin-bottom: 5px;">Developer Advocate</h4>\n                <p style="color: #a1a1aa; margin: 0;">Marketing ‚Ä¢ Remote (Global)</p>\n            </div>\n            <a href="#" style="color: #818cf8; text-decoration: none; font-weight: 600;">Apply &rarr;</a>\n        </div>\n\n    </div>\n</div>','\n',char(10)),'2025-12-14 00:48:39','2025-12-14 00:48:39',NULL,'page',NULL,'default_site','Join the Gaia Alpha team. We are looking for talented engineers, designers, and product managers.','careers, jobs, hiring, engineering, design, tech jobs');
INSERT INTO cms_pages VALUES(4,1,'Contact Us','contact',replace('<div class="contact-wrapper">\n    <p class="lead" style="font-size: 1.25rem; color: #a1a1aa; text-align: center; max-width: 600px; margin: 0 auto 60px;">Whether you have a question about features, pricing, or need a custom solution, our team is ready to answer all your questions.</p>\n\n    <div style="display: grid; grid-template-columns: repeat(auto-fit, minmax(300px, 1fr)); gap: 60px;">\n        \n        <!-- Contact Info -->\n        <div>\n            <div style="margin-bottom: 40px;">\n                <h3 style="color: #fff; margin-bottom: 20px; font-size: 1.5rem;">Headquarters</h3>\n                <p style="color: #a1a1aa; line-height: 1.6;">\n                    <strong>Gaia Alpha Inc.</strong><br>\n                    100 Innovation Boulevard, Suite 500<br>\n                    San Francisco, CA 94103<br>\n                    United States\n                </p>\n            </div>\n\n            <div style="margin-bottom: 40px;">\n                <h3 style="color: #fff; margin-bottom: 20px; font-size: 1.5rem;">Direct Contact</h3>\n                <ul style="list-style: none; padding: 0; color: #a1a1aa; line-height: 2;">\n                    <li style="display: flex; align-items: center; gap: 10px;">üìß <a href="mailto:hello@gaia-alpha.com" style="color: #818cf8; text-decoration: none;">hello@gaia-alpha.com</a></li>\n                    <li style="display: flex; align-items: center; gap: 10px;">üíº <a href="mailto:sales@gaia-alpha.com" style="color: #818cf8; text-decoration: none;">sales@gaia-alpha.com</a></li>\n                    <li style="display: flex; align-items: center; gap: 10px;">üìû +1 (555) 012-3456</li>\n                </ul>\n            </div>\n\n             <div>\n                <h3 style="color: #fff; margin-bottom: 20px; font-size: 1.5rem;">Global Offices</h3>\n                <div style="display: grid; grid-template-columns: 1fr 1fr; gap: 20px;">\n                    <div>\n                        <h4 style="color: #fff; font-size: 1rem;">London</h4>\n                        <p style="color: #a1a1aa; font-size: 0.9rem;">12 Clerkenwell Road</p>\n                    </div>\n                    <div>\n                         <h4 style="color: #fff; font-size: 1rem;">Tokyo</h4>\n                        <p style="color: #a1a1aa; font-size: 0.9rem;">Shibuya Scramble Sq.</p>\n                    </div>\n                </div>\n             </div>\n        </div>\n\n        <!-- Placeholder Form -->\n        <div style="background: rgba(255,255,255,0.03); padding: 40px; border-radius: 16px; border: 1px solid rgba(255,255,255,0.05);">\n            <h3 style="color: #fff; margin-bottom: 30px; font-size: 1.5rem;">Send us a message</h3>\n            <form onsubmit="event.preventDefault(); alert(''This is a demo form.'');">\n                <div style="margin-bottom: 20px;">\n                    <label style="display: block; color: #d1d5db; margin-bottom: 8px; font-weight: 500;">Name</label>\n                    <input type="text" placeholder="Jane Doe" style="width: 100%; padding: 12px; background: rgba(0,0,0,0.3); border: 1px solid rgba(255,255,255,0.1); border-radius: 6px; color: #fff;">\n                </div>\n                <div style="margin-bottom: 20px;">\n                    <label style="display: block; color: #d1d5db; margin-bottom: 8px; font-weight: 500;">Email</label>\n                    <input type="email" placeholder="jane@company.com" style="width: 100%; padding: 12px; background: rgba(0,0,0,0.3); border: 1px solid rgba(255,255,255,0.1); border-radius: 6px; color: #fff;">\n                </div>\n                <div style="margin-bottom: 30px;">\n                    <label style="display: block; color: #d1d5db; margin-bottom: 8px; font-weight: 500;">Message</label>\n                    <textarea rows="4" placeholder="How can we help?" style="width: 100%; padding: 12px; background: rgba(0,0,0,0.3); border: 1px solid rgba(255,255,255,0.1); border-radius: 6px; color: #fff;"></textarea>\n                </div>\n                <button type="submit" style="width: 100%; background: #6366f1; color: white; padding: 12px; border: none; border-radius: 6px; font-weight: 600; cursor: pointer; transition: background 0.2s;">Send Message</button>\n            </form>\n        </div>\n    </div>\n</div>','\n',char(10)),'2025-12-14 00:48:39','2025-12-14 00:48:39',NULL,'page',NULL,'default_site','Get in touch with Gaia Alpha for support, sales, or general inquiries. We are here to help.','contact, support, sales, office location, email, phone');
INSERT INTO cms_pages VALUES(5,1,'Home','home','<div class=''hero''><h1>Welcome to Gaia Alpha</h1><p class=''lead''>The unified open-source operating system for your digital life.</p><a href=''/app'' class=''btn btn-primary''>Launch App</a></div><div class=''features''><h2>Why Choose Us?</h2><p>Speed, Simplicity, and Security.</p></div>','2025-12-14 00:48:39','2025-12-14 00:48:39',NULL,'page',NULL,'default_site','Welcome to Gaia Alpha - The unified open-source operating system for your digital life. Fast, secure, and modern platform for enterprise.','gaia alpha, enterprise, software, open source, framework');
INSERT INTO cms_pages VALUES(6,1,'Privacy Policy','privacy',replace('<div class="legal-wrapper" style="max-width: 800px; margin: 0 auto;">\n    <p style="color: #a1a1aa; margin-bottom: 40px;">Last Updated: <strong>December 15, 2025</strong></p>\n\n    <section style="margin-bottom: 40px;">\n        <h3 style="color: #fff; font-size: 1.5rem; margin-bottom: 15px;">1. Introduction</h3>\n        <p style="color: #a1a1aa; line-height: 1.7; margin-bottom: 15px;">Welcome to Gaia Alpha. We respect your privacy and are committed to protecting your personal data. This privacy policy will inform you as to how we look after your personal data when you visit our website (regardless of where you visit it from) and tell you about your privacy rights and how the law protects you.</p>\n    </section>\n\n    <section style="margin-bottom: 40px;">\n        <h3 style="color: #fff; font-size: 1.5rem; margin-bottom: 15px;">2. The Data We Collect</h3>\n        <p style="color: #a1a1aa; line-height: 1.7; margin-bottom: 15px;">We may collect, use, store and transfer different kinds of personal data about you which we have grouped together follows:</p>\n        <ul style="color: #a1a1aa; padding-left: 20px; line-height: 1.7;">\n            <li><strong>Identity Data</strong> includes first name, last name, username or similar identifier.</li>\n            <li><strong>Contact Data</strong> includes email address and telephone numbers.</li>\n            <li><strong>Technical Data</strong> includes internet protocol (IP) address, your login data, browser type and version.</li>\n        </ul>\n    </section>\n\n    <section style="margin-bottom: 40px;">\n        <h3 style="color: #fff; font-size: 1.5rem; margin-bottom: 15px;">3. How We Use Your Data</h3>\n        <p style="color: #a1a1aa; line-height: 1.7; margin-bottom: 15px;">We will only use your personal data when the law allows us to. Most commonly, we will use your personal data in the following circumstances:</p>\n        <ul style="color: #a1a1aa; padding-left: 20px; line-height: 1.7;">\n            <li>Where we need to perform the contract we are about to enter into or have entered into with you.</li>\n            <li>Where it is necessary for our legitimate interests (or those of a third party) and your interests and fundamental rights do not override those interests.</li>\n            <li>Where we need to comply with a legal or regulatory obligation.</li>\n        </ul>\n    </section>\n\n    <section>\n        <h3 style="color: #fff; font-size: 1.5rem; margin-bottom: 15px;">4. Contact Us</h3>\n        <p style="color: #a1a1aa; line-height: 1.7; margin-bottom: 15px;">If you have any questions about this privacy policy or our privacy practices, please contact us at: <a href="mailto:privacy@gaia-alpha.com" style="color: #818cf8;">privacy@gaia-alpha.com</a>.</p>\n    </section>\n</div>','\n',char(10)),'2025-12-14 00:48:39','2025-12-14 00:48:39',NULL,'page',NULL,'default_site','Read Gaia Alpha''s Privacy Policy. We are committed to protecting your personal data.','privacy policy, data protection, gdpr, ccpa');
INSERT INTO cms_pages VALUES(7,1,'Our Services','services',replace('<div class="services-wrapper">\n    <p class="lead" style="font-size: 1.25rem; color: #a1a1aa; max-width: 800px; margin: 0 auto 60px; text-align: center;">From rapid prototyping to global scale, we provide the technical expertise and strategic guidance to help you build better software, faster.</p>\n\n    <div style="display: grid; grid-template-columns: repeat(auto-fit, minmax(300px, 1fr)); gap: 40px; margin-bottom: 80px;">\n        <div style="background: rgba(255,255,255,0.03); padding: 40px; border-radius: 16px; border: 1px solid rgba(255,255,255,0.05); transition: transform 0.2s;">\n            <div style="font-size: 2.5rem; margin-bottom: 20px;">üí°</div>\n            <h3 style="color: #fff; margin-bottom: 15px; font-size: 1.5rem;">Strategic Consulting</h3>\n            <p style="color: #a1a1aa; line-height: 1.6; margin-bottom: 20px;">Navigate the complex digital landscape with confidence. We help you identify opportunities, mitigate risks, and design a roadmap for digital transformation.</p>\n            <ul style="color: #a1a1aa; padding-left: 20px; line-height: 1.8;">\n                <li>Technology Audit & Assessment</li>\n                <li>Architecture Design</li>\n                <li>Digital Strategy</li>\n            </ul>\n        </div>\n\n        <div style="background: rgba(255,255,255,0.03); padding: 40px; border-radius: 16px; border: 1px solid rgba(255,255,255,0.05); transition: transform 0.2s;">\n            <div style="font-size: 2.5rem; margin-bottom: 20px;">üíª</div>\n            <h3 style="color: #fff; margin-bottom: 15px; font-size: 1.5rem;">Custom Development</h3>\n            <p style="color: #a1a1aa; line-height: 1.6; margin-bottom: 20px;">Build software that fits your business perfectly. Our expert engineers deliver high-performance, scalable, and secure applications tailored to your needs.</p>\n             <ul style="color: #a1a1aa; padding-left: 20px; line-height: 1.8;">\n                <li>Full-Stack Web Development</li>\n                <li>API Development & Integration</li>\n                <li>Legacy System Modernization</li>\n            </ul>\n        </div>\n\n        <div style="background: rgba(255,255,255,0.03); padding: 40px; border-radius: 16px; border: 1px solid rgba(255,255,255,0.05); transition: transform 0.2s;">\n            <div style="font-size: 2.5rem; margin-bottom: 20px;">‚òÅÔ∏è</div>\n            <h3 style="color: #fff; margin-bottom: 15px; font-size: 1.5rem;">Cloud Solutions</h3>\n            <p style="color: #a1a1aa; line-height: 1.6; margin-bottom: 20px;">Scale without limits. We help you leverage the power of the cloud to reduce costs, improve reliability, and accelerate deployment.</p>\n             <ul style="color: #a1a1aa; padding-left: 20px; line-height: 1.8;">\n                <li>Cloud Migration (AWS/Azure/GCP)</li>\n                <li>Docker & Kubernetes Setup</li>\n                <li>CI/CD Pipeline Automation</li>\n            </ul>\n        </div>\n    </div>\n\n    <div style="background: linear-gradient(135deg, rgba(99, 102, 241, 0.1) 0%, rgba(16, 185, 129, 0.1) 100%); padding: 60px; border-radius: 20px; text-align: center; border: 1px solid rgba(255,255,255,0.1);">\n        <h2 style="color: #fff; margin-bottom: 20px; font-size: 2rem;">Ready to transform your business?</h2>\n        <p style="color: #d1d5db; margin-bottom: 30px; font-size: 1.1rem;">Let''s discuss how Gaia Alpha can help you achieve your goals.</p>\n        <a href="/page/contact" style="display: inline-block; background: #6366f1; color: white; padding: 12px 30px; border-radius: 8px; text-decoration: none; font-weight: 600; transition: background 0.2s;">Get in Touch</a>\n    </div>\n</div>','\n',char(10)),'2025-12-14 00:48:39','2025-12-14 00:48:39',NULL,'page',NULL,'default_site','Explore Gaia Alpha''s enterprise services: Cloud Architecture, Custom Development, and Strategic Consulting.','services, consulting, development, cloud, enterprise software');
INSERT INTO cms_pages VALUES(8,1,'Terms of Service','terms',replace('<div class="legal-wrapper" style="max-width: 800px; margin: 0 auto;">\n    <p style="color: #a1a1aa; margin-bottom: 40px;">Last Updated: <strong>December 15, 2025</strong></p>\n\n    <section style="margin-bottom: 40px;">\n        <h3 style="color: #fff; font-size: 1.5rem; margin-bottom: 15px;">1. Agreement to Terms</h3>\n        <p style="color: #a1a1aa; line-height: 1.7; margin-bottom: 15px;">By accessing our website or using our services, you agree to be bound by these Terms of Service and all applicable laws and regulations. If you do not agree with any of these terms, you are prohibited from using or accessing this site.</p>\n    </section>\n\n    <section style="margin-bottom: 40px;">\n        <h3 style="color: #fff; font-size: 1.5rem; margin-bottom: 15px;">2. Use License</h3>\n        <p style="color: #a1a1aa; line-height: 1.7; margin-bottom: 15px;">Permission is granted to temporarily download one copy of the materials (information or software) on Gaia Alpha''s website for personal, non-commercial transitory viewing only. This is the grant of a license, not a transfer of title, and under this license you may not:</p>\n        <ul style="color: #a1a1aa; padding-left: 20px; line-height: 1.7;">\n            <li>Modify or copy the materials;</li>\n            <li>Use the materials for any commercial purpose, or for any public display (commercial or non-commercial);</li>\n            <li>Attempt to decompile or reverse engineer any software contained on Gaia Alpha''s website;</li>\n            <li>Remove any copyright or other proprietary notations from the materials; or</li>\n            <li>Transfer the materials to another person or "mirror" the materials on any other server.</li>\n        </ul>\n    </section>\n\n    <section style="margin-bottom: 40px;">\n        <h3 style="color: #fff; font-size: 1.5rem; margin-bottom: 15px;">3. Disclaimer</h3>\n        <p style="color: #a1a1aa; line-height: 1.7; margin-bottom: 15px;">The materials on Gaia Alpha''s website are provided on an ''as is'' basis. Gaia Alpha makes no warranties, expressed or implied, and hereby disclaims and negates all other warranties including, without limitation, implied warranties or conditions of merchantability, fitness for a particular purpose, or non-infringement of intellectual property or other violation of rights.</p>\n    </section>\n\n    <section>\n        <h3 style="color: #fff; font-size: 1.5rem; margin-bottom: 15px;">4. Governing Law</h3>\n        <p style="color: #a1a1aa; line-height: 1.7; margin-bottom: 15px;">These terms and conditions are governed by and construed in accordance with the laws of California and you irrevocably submit to the exclusive jurisdiction of the courts in that State or location.</p>\n    </section>\n</div>','\n',char(10)),'2025-12-14 00:48:40','2025-12-14 00:48:40',NULL,'page',NULL,'default_site','Gaia Alpha Terms of Service. Please read these terms carefully before using our services.','terms of service, legal, user agreement, conditions');
