CREATE TABLE messages (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    sender_id INTEGER NOT NULL,
    receiver_id INTEGER NOT NULL,
    content TEXT NOT NULL,
    is_read BOOLEAN DEFAULT 0,
    created_at DATETIME DEFAULT CURRENT_TIMESTAMP,
    FOREIGN KEY(sender_id) REFERENCES users(id),
    FOREIGN KEY(receiver_id) REFERENCES users(id)
);
INSERT INTO messages VALUES(1,1,1,'Welcome to your new Gaia Alpha instance! This is your first message.',0,'2025-12-14 00:48:40');
CREATE TABLE cms_partials (
    id INTEGER PRIMARY KEY AUTOINCREMENT,
    user_id INTEGER NOT NULL,
    name VARCHAR(255) NOT NULL,
    content TEXT,
    created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    UNIQUE(name)
);
INSERT INTO cms_partials VALUES(1,1,'site_header',replace('<?php\n// templates/layout/header.php\n// Expected variables: $title, $desc, $keywords, $page (optional)\n$page = $page ?? null;\n$title = $title ?? ($page[''title''] ?? ''Gaia Alpha'');\n$desc = $desc ?? ($page[''meta_description''] ?? ''Gaia Alpha - Enterprise Solution'');\n$keywords = $keywords ?? ($page[''meta_keywords''] ?? '''');\n\n// Fetch Menus for Navigation\n$mainMenu = \GaiaAlpha\Model\Menu::findByLocation(''main'');\n$navItems = $mainMenu ? json_decode($mainMenu[''items''], true) : [];\n?>\n<!DOCTYPE html>\n<html lang="en">\n\n<head>\n    <meta charset="UTF-8">\n    <meta name="viewport" content="width=device-width, initial-scale=1.0">\n    <title><?= htmlspecialchars($title) ?></title>\n    <meta name="description" content="<?= htmlspecialchars($desc) ?>">\n    <?php if ($keywords): ?>\n        <meta name="keywords" content="<?= htmlspecialchars($keywords) ?>">\n    <?php endif; ?>\n    <?php if (isset($page[''image''])): ?>\n        <meta property="og:image" content="<?= htmlspecialchars($page[''image'']) ?>">\n    <?php endif; ?>\n\n    <link rel="stylesheet" href="<?= \GaiaAlpha\Asset::url(''/css/site.css'') ?>">\n    <style>\n        /* Shared Header/Footer Styles (Ideally move to site.css) */\n\n        /* Navigation */\n        .site-header {\n            position: fixed;\n            top: 0;\n            left: 0;\n            right: 0;\n            z-index: 1000;\n            background: rgba(9, 9, 11, 0.8);\n            backdrop-filter: blur(12px);\n            border-bottom: 1px solid var(--border-color);\n            padding: var(--space-md) var(--space-lg);\n            display: flex;\n            justify-content: space-between;\n            align-items: center;\n        }\n\n        .site-brand {\n            font-size: 1.5rem;\n            font-weight: 700;\n            color: var(--text-primary);\n            text-decoration: none;\n            letter-spacing: -0.02em;\n        }\n\n        .site-nav {\n            display: flex;\n            gap: var(--space-lg);\n            align-items: center;\n        }\n\n        .nav-link {\n            color: var(--text-secondary);\n            text-decoration: none;\n            font-weight: 500;\n            transition: color 0.2s;\n            font-size: 0.95rem;\n        }\n\n        .nav-link:hover {\n            color: var(--text-primary);\n        }\n\n        .nav-cta {\n            background: var(--accent-color);\n            color: white;\n            padding: 8px 20px;\n            border-radius: var(--radius-md);\n            text-decoration: none;\n            font-weight: 600;\n            transition: transform 0.2s, box-shadow 0.2s;\n        }\n\n        .nav-cta:hover {\n            transform: translateY(-2px);\n            box-shadow: 0 4px 12px rgba(99, 102, 241, 0.3);\n        }\n\n        /* Mobile */\n        @media (max-width: 768px) {\n            .site-header {\n                flex-direction: column;\n                gap: 15px;\n            }\n\n            .site-nav {\n                gap: 15px;\n                flex-wrap: wrap;\n                justify-content: center;\n            }\n        }\n\n        /* Body & Layout */\n        body {\n            /* Ensure footer stays at bottom */\n            display: flex;\n            flex-direction: column;\n            min-height: 100vh;\n        }\n\n        main {\n            flex: 1;\n        }\n    </style>\n</head>\n\n<body>\n    <!-- Navigation -->\n    <header class="site-header">\n        <a href="/" class="site-brand">Gaia Alpha</a>\n        <nav class="site-nav">\n            <?php foreach ($navItems as $item): ?>\n                <?php if (($item[''text''] ?? '''') === ''App Dashboard'')\n                    continue; ?>\n                <a href="<?= htmlspecialchars($item[''href'']) ?>" class="nav-link"><?= htmlspecialchars($item[''text'']) ?></a>\n            <?php endforeach; ?>\n            <a href="/app" class="nav-cta">Launch App</a>\n        </nav>\n    </header>\n\n    <!-- Main Content Wrapper -->\n    <main>','\n',char(10)),'2025-12-14 00:48:39','2025-12-14 00:48:39');
INSERT INTO cms_partials VALUES(2,1,'site_footer',replace('<?php\n// templates/layout/footer.php\n$footerMenu = \GaiaAlpha\Model\Menu::findByLocation(''footer'');\n$footerItems = $footerMenu ? json_decode($footerMenu[''items''], true) : [];\n?>\n</main>\n\n<!-- Footer -->\n<style>\n    .site-footer {\n        padding: 80px 20px 40px;\n        border-top: 1px solid var(--border-color);\n        background: #050507;\n        margin-top: auto;\n        /* Push to bottom if content is short */\n    }\n\n    .footer-grid {\n        display: grid;\n        grid-template-columns: 2fr 1fr 1fr 1fr;\n        gap: 40px;\n        max-width: 1200px;\n        margin: 0 auto 60px;\n    }\n\n    .footer-brand h2 {\n        margin-bottom: 20px;\n        font-size: 1.5rem;\n        color: var(--text-primary);\n    }\n\n    .footer-col h4 {\n        color: var(--text-primary);\n        margin-bottom: 20px;\n        font-weight: 600;\n    }\n\n    .footer-link {\n        display: block;\n        color: var(--text-secondary);\n        text-decoration: none;\n        margin-bottom: 12px;\n        transition: color 0.2s;\n    }\n\n    .footer-link:hover {\n        color: var(--text-primary);\n    }\n\n    .footer-bottom {\n        max-width: 1200px;\n        margin: 0 auto;\n        padding-top: 40px;\n        border-top: 1px solid var(--border-color);\n        display: flex;\n        justify-content: space-between;\n        color: var(--text-muted);\n        font-size: 0.9rem;\n    }\n\n    @media (max-width: 768px) {\n        .footer-grid {\n            grid-template-columns: 1fr;\n        }\n    }\n</style>\n\n<footer class="site-footer">\n    <div class="footer-grid">\n        <div class="footer-col footer-brand">\n            <h2>Gaia Alpha</h2>\n            <p class="text-secondary" style="max-width: 300px;">The open-source operating system for the modern web.\n                Empowering builders to create without limits.</p>\n        </div>\n        <div class="footer-col">\n            <h4>Product</h4>\n            <a href="#" class="footer-link">Features</a>\n            <a href="#" class="footer-link">Integration</a>\n            <a href="#" class="footer-link">Enterprise</a>\n            <a href="#" class="footer-link">Changelog</a>\n        </div>\n        <div class="footer-col">\n            <h4>Resources</h4>\n            <a href="#" class="footer-link">Documentation</a>\n            <a href="#" class="footer-link">API Reference</a>\n            <a href="#" class="footer-link">Community</a>\n            <a href="#" class="footer-link">Blog</a>\n        </div>\n        <div class="footer-col">\n            <h4>Company</h4>\n            <?php foreach ($footerItems as $item): ?>\n                <a href="<?= htmlspecialchars($item[''href'']) ?>"\n                    class="footer-link"><?= htmlspecialchars($item[''text'']) ?></a>\n            <?php endforeach; ?>\n            <a href="/page/contact" class="footer-link">Contact</a>\n        </div>\n    </div>\n    <div class="footer-bottom">\n        <span>&copy; <?= date(''Y'') ?> Gaia Alpha. All rights reserved.</span>\n        <div style="display: flex; gap: 20px;">\n            <a href="#" style="color:inherit; text-decoration:none;">Twitter</a>\n            <a href="#" style="color:inherit; text-decoration:none;">GitHub</a>\n            <a href="#" style="color:inherit; text-decoration:none;">Discord</a>\n        </div>\n    </div>\n</footer>\n</body>\n\n</html>','\n',char(10)),'2025-12-14 00:48:39','2025-12-14 00:48:39');
